/**
 * @param {string} s
 * @return {number}
 */

function longestPalindrome(s) {
  const set = new Set();
  let result = 0;

  for (const ele of s) {
    if (set.has(ele)) {
      set.delete(ele);
      result += 2;
    } else {
      set.add(ele);
    }
  }

  if (set.size) return result + 1;
  return result;
}

console.log(
  longestPalindrome(
    ""
  )
);
